if ("visibilityState" in document) {
	document.addEventListener("DOMContentLoaded", function () {
		var each = Array.prototype.forEach;

		function initCatSelectBox(form) {
			function submitForm() {
				this.form.submit();
			}

			each.call(form.getElementsByTagName("select"), function (select) {
				select.addEventListener("change", submitForm);
			});
			each.call(form.getElementsByClassName("xhsShopButton"), function (button) {
				button.style = "display:none";
			});
		}

		function initProductsTable(table) {
			function confirmDeletion(event) {
				var text = table.getAttribute("data-delete");
				var message = text.replace("%s", this.getAttribute("data-name"));
				if (!confirm(message)) {
					event.preventDefault();
				}
			}

			function rowOfForm(form) {
				var element = form.parentNode;
		
				while (element.tagName !== "TR") {
					element = element.parentNode;
				}
				return element;
			}

			function serialize(form) {
				var params = [];
				each.call(form.elements, function (element) {
					if (!element.name) {
						return;
					}
					params.push(element.name + "=" + encodeURIComponent(element.value));
				});
				return params.join("&");
			}

			function submit(form) {
				var request = new XMLHttpRequest;
				request.open("POST", form.action, true);
				request.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
				request.setRequestHeader("X-Requested-With", "XMLHttpRequest");
				request.send(serialize(form));
			}

			function fixSwapIds() {
				var forms = document.getElementsByClassName("xhsMoveUp");
				var form;
				if ((form = forms[0])) {
					form.xhsProductSwapID.value = "";
					form.style.display = "none";
				}
				for (var i = 1; i < forms.length; i++) {
					form = forms[i];
					form.xhsProductSwapID.value = forms[i-1].xhsProductID.value;
					form.style.display = "";
				}
				var forms = document.getElementsByClassName("xhsMoveDown");
				for (var i = 0; i < forms.length - 1; i++) {
					form = forms[i];
					form.xhsProductSwapID.value = forms[i+1].xhsProductID.value;
					form.style.display = "";
				}
				if ((form = forms[forms.length-1])) {
					form.xhsProductSwapID.value = "";
					form.style.display = "none";
				}
			}

			function moveUp(event) {
				var thisRow = rowOfForm(this);
				submit(this);
				var thatRow = thisRow.previousElementSibling;
				thisRow.parentNode.insertBefore(thisRow, thatRow);
				fixSwapIds();
				event.preventDefault();
			}

			function moveDown(event) {
				var thisRow = rowOfForm(this);
				submit(this);
				var thatRow = thisRow.nextElementSibling;
				thisRow.parentNode.insertBefore(thatRow, thisRow);
				fixSwapIds();
				event.preventDefault();
			}

			each.call(document.getElementsByClassName("xhsMoveUp"), function (form) {
				form.addEventListener("submit", moveUp);
			});
			each.call(document.getElementsByClassName("xhsMoveDown"), function (form) {
				form.addEventListener("submit", moveDown);
			});
			each.call(document.getElementsByClassName("xhsDeleteProduct"), function (form) {
				form.addEventListener("submit", confirmDeletion);
			});
		}

		function initProductForm(form) {
			function onSelectChange() {
				var pic = this.value;
				var data = JSON.parse(this.getAttribute("data-xhs"));
				var path = data.path;
				var element = document.getElementById(data.element);
				
				if (/\.(?:jpg|jpeg|png|tif|gif|tiff|svg)$/.test(pic)) {
					each.call(element.getElementsByTagName("a"), function (anchor) {
						each.call(anchor.getElementsByTagName("img"), function (image) {
							anchor.href = image.src = path + pic;
						});
					});
				}
			}
	
			each.call(form.getElementsByClassName("xhsPicSelector"), function (select) {
				select.addEventListener("change", onSelectChange);
			});
		}

		each.call(document.getElementsByClassName("catSelectBox"), initCatSelectBox);
		each.call(document.querySelectorAll("#xhsProductsTable"), initProductsTable);
		each.call(document.getElementsByClassName("xhsProductForm"), initProductForm);
	});
}
